package com.nativenavj.domain;

/**
 * Intermediate control targets generated by the Computer.
 * Represents the desired physical orientation and power.
 */
public record Target(
        double roll,
        double pitch,
        double yaw,
        double throttle) {
    /**
     * Creates a neutral/default target.
     */
    public static Target neutral() {
        return new Target(0.0, 0.0, 0.0, 0.0);
    }
}
